const lista = document.getElementById('listaNombres');
const requestURL = 'data.json';
const request = new XMLHttpRequest();

request.open('GET', requestURL);
request.responseType = 'json';
request.send();

const onLoadFunction = function() {
     const personas = request.response;
     printNewData(personas);
     showNewData(personas);
}

request.onload = onLoadFunction;

function printNewData(jsonObj) {
     const newP = document.createElement('p');
     newP.textContent = jsonObj['titulo'];
     lista.appendChild(newP);
}

function showNewData(jsonObj) {
     const miembros = jsonObj['peranas'];

     for (let i = 0; i < miembros.length; i++) {
          const newArticle = document.createElement('article');
          const newP = document.createElement('p');
          const newList = document.createElement('ul');

          newP.textContent = miembros[i].nombre;
          newArticle.appendChild(newP);

     }
}

module.exports = onLoadFunction;
